import axios from 'axios'
import React, { useEffect } from 'react'
import { Card, Col } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { API_URL } from '../../utils/constant'

const Home = (homeIsi) => {



    useEffect(() => {
        axios.get( API_URL +  "users")
      .then(res => {
        const menus = res.data;
        console.log(menus);
        this.setState({ menus });
      }).catch((error) =>{
          console.log(error);
      })
    }, [])

    return (
        <div>
            <div>
                <Link to={'/detail/' + homeIsi.id}>
                    <Col md={4} xs={6} className="mb-4 mt-4 ml-4">
                        <Card style={{ width: '18rem' }} className="shadow">
                            <Card.Img variant="top" src={"assets/images/nama/" + homeIsi.gambar} />
                            <Card.Body>
                                <Card.Title>{homeIsi.name} </Card.Title>
                                <Card.Text>
                                    {homeIsi.username}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                </Link>
            </div>
        </div>
    )
}

export default Home;
